import React, { Component } from 'react';
import Week from './Week';
import moment from 'moment';
import DayNames from './DayNames';
import styled from 'styled-components';


const MonthLabelContainer = styled.div`
  display:flex;
  justify-content:center;
`;
const Arrows = styled.i`
  font-size: 4rem;
  margin: auto 0;
  padding: 0rem 3rem;
`;

const MonthLabel = styled.h1`
  margin: 0 auto 2rem;
  font-size: 7rem;
`;
export class WeekView extends Component {
    constructor(props) {
        super(props);
        this.state = {
          month: moment(),
          selected: moment().startOf('day')
        }
    }

    previous = () => {
      const { month } = this.state;

      this.setState({
        month: month.subtract(1, 'month'),
      });
    }

    next = () => {
      const { month } = this.state;

      this.setState({
        month: month.add(1,'month'),
      });
    }
    renderWeekLabel() {
      const { month } = this.state;

      return <span className="month-label">{month.startOf('week').fromNow("MMMM Do YYYY")}</span>;
    }
  render() {
    const { selected, month } = this.state;
    let date = this.state.month.clone().startOf("month").add("w" -1).day("Sunday");

    return (
      <div>
        <MonthLabelContainer>
        <Arrows className="arrow fa fa-angle-left" onClick={this.previous}/>
          <MonthLabel>{this.renderWeekLabel()}</MonthLabel>
        <Arrows className="arrow fa fa-angle-right" onClick={this.next}/>
        </MonthLabelContainer>
        <DayNames />
        <Week key={date} 
        date={date.clone()} 
        month={month} 
        select={(day)=>this.select(day)} 
        selected={selected}/>
      </div>
    )
  }
}

export default WeekView
